package com.arraylistprograms;

class Bike {
	String color;
    int mileage;
	public Bike() {
		// TODO Auto-generated constructor stub
	}
	public Bike(String color, int mileage) {
		super();
		this.color = color;
		this.mileage = mileage;
	}
	@Override
	public String toString() {
		return "Bike [color=" + color + ", mileage=" + mileage + "]";
	}
	
	

}
